<script setup lang="ts">
import * as notivue from "notivue";

import {useAuthModalStore} from "~/stores/auth/modal/singIn";
import {useAuthStore} from "~/stores/auth";

const router = useRouter();
const route = useRoute();
const authStore = useAuthStore();
const authModalStore = useAuthModalStore();

let data = reactive({
  userData: null
});

function logoutUser() {
  notivue.push.warning({
    title: "Аккаунт",
    message: "Выход из аккаунта!"
  })
  authStore.logoutUser();
}

// watch(authStore.GetUserData, (first: any, second: any) => {
//   console.log(authStore.GetUserData)
//   data.userData = authStore.GetUserData;
// })


watch(() => authStore.getUserData, (last: any, next: any) => {
  console.log(next)
  data.userData = next;
})


const userData = computed(() => authStore.getUserData);
const userDataGroup = computed(() => {
  const data = userData.value;  // Используем .value для доступа к значению computed
  return data && data.groups ? data.groups : [];
});

const userTextGroups = computed(() => {
  const groups = userDataGroup.value;  // Используем .value для доступа к значению computed
  if (Array.isArray(groups)) {
    return groups.map(item => item.name).join(',');
  } else {
    return '';
  }
});
</script>

